# Total Accepted:    288.7K
# Total Submissions: 1M
# Testcase Example:  '"(()"'
#
# Given a string containing just the characters '(' and ')', find the length of
# the longest valid (well-formed) parentheses substring.
#
# Example 1:
#
#
# Input: "(()"
# Output: 2
# Explanation: The longest valid parentheses substring is "()"
#
#
# Example 2:
#
#
# Input: ")()())"
# Output: 4
# Explanation: The longest valid parentheses substring is "()()"
#
#


from pprint import pprint

# @lc code=start
class Solution:
    def longestValidParentheses(self, s: str) -> int:
        if not s:
            return 0

        # max_lens 是一个一维数组。max_lens[i] 表示以 s 的第 i 项开始，最长的合法子字符串的长度是多少
        # 其中：
        #   0 <= i < len(s)
        max_lens = [-1] * len(s)

        # 填充并返回 max_lens[i] 的结果
        def get_max_lens(i: int) -> int:
            if i >= len(s):
                return 0

            # assert 0 <= i < len(s)

            if max_lens[i] != -1:
                return max_lens[i]

            if i == len(s) - 1:
                max_lens[i] = 0
            elif s[i] == ")":
                max_lens[i] = 0
            elif s[i] == "(":
                if s[i + 1] == ")":
                    max_lens[i] = 2 + get_max_lens(i + 2)
                else:
                    next_len = get_max_lens(i + 1)
                    if next_len == 0:
                        max_lens[i] = 0
                    elif i + next_len + 1 < len(s) and s[i + next_len + 1] == ")":
                        max_lens[i] = next_len + 2 + get_max_lens(i + next_len + 2)
                    else:
                        max_lens[i] = 0

            # assert max_lens[i] >= 0
            return max_lens[i]

        best = 0
        for i in range(len(s) - 1, -1, -1):
            best = max(best, get_max_lens(i))
        return best

        ########################################################################

        '''
        # dp 是一个二维数组，dp[i][j] 表示 s[i:j+1] 是否是一个合法的子字符串。这个子字符串的长度是 j - i + i
        # 其中：
        #   0 <= i <= j < len(s)
        dp = []
        for _ in range(len(s)):
            dp.append([False] * len(s))

        # l == j - i + 1 == 2（子字符串包含两个字符）
        for j in range(1, len(s)):
            i = j - 1
            if s[i] == "(" and s[j] == ")":
                dp[i][j] = True
                max_lens[i] = 2

        # l == j - i + 1 >= 3
        for l in range(3, len(s) + 1):

            # 只有子字符串包含偶数个字符时候（也就是 l 为偶数），才有可能是合法的
            if l % 2 != 0:
                continue

            for j in range(l - 1, len(s)):
                i = j - l + 1

                if not (s[i] == "(" and s[j] == ")"):
                    continue

                """
                (  (     )  (    )     )
                i  i+1           j-1   j
                """
                if dp[i + 1][j - 1] is True and s[i] == "(" and s[j] == ")":
                    dp[i][j] = True
                    max_lens[i] = max(max_lens[i], j - i + 1)

                """
                ( ... ) ( ... )
                i     n m     j

                在这种情况下，dp[i][j] 正确的条件是 dp[i][n] 和 dp[m][j] 正确
                """

                n = i + max_lens[i] - 1
                if i < n < j:
                    m = n + 1
                    assert i < n < m < j
                    assert dp[i][n]
                    assert s[n] == ")"

                    if dp[m][j]:
                        dp[i][j] = True
                        max_lens[i] = max(max_lens[i], j - i + 1)


        # pprint(dp)
        return max(max_lens)
        '''


